 case HandStatus::BLACKJACK: std::cout << " [BLACKJACK]";
 break;
 case HandStatus::SURRENDERED: std::cout << " [SURRENDERED]";
 break;
 case HandStatus::DOUBLED: std::cout << " [DOUBLED]";
