+std::string Card::toString() const {
+ std::string rank;
+ if (value == 1) rank = "A";
+ else if (value == 11) rank = "J";
+ else if (value == 12) rank = "Q";
+ else if (value == 13) rank = "K";
+ else rank = std::to_string(value);
+
+ std::string suitStr;
+ switch (suit) {
+ case Suit::HEARTS: suitStr = "♥"; break;
+ case Suit::DIAMONDS: suitStr = "♦"; break;
+ case Suit::CLUBS: suitStr = "♣"; break;
+ case Suit::SPADES: suitStr = "♠"; break;
+ }
+ 
+ return rank + suitStr;
+}
