+#include "Hand.h"

+void Hand::addCard(const Card& card) {
+ cards.push_back(card);
+}
+
+int Hand::getValue() const {
+ int value = 0;
+ int aces = 0;
+ 
+ for (const auto& card : cards) {
+ int cardValue = card.getValue();
+ if (cardValue == 1) {
+ aces++;
+ value += 11;
+ } else if (cardValue > 10) {
+ value += 10;
+ } else {
+ value += cardValue;
+ }
+ }
